import { Component, OnInit } from '@angular/core';
import { FormGroup,  FormBuilder,  Validators } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent  {
  angForm!: FormGroup;

  constructor(private fb: FormBuilder) {
    this.createForm();
  }

  ngOnInit() {
  }

  createForm() {
    this.angForm = this.fb.group({
      name: ['', Validators.required],
      age: ['', Validators.required],
      address: ['', Validators.required]
    });
  }
}
<div style="text-align:center">
  <form [formGroup]="angForm" novalidate>
    <div class="form-group">
      <label>Name:
        <input class="form-control" formControlName="name">
        <div *ngIf="angForm.get('name')?.invalid && (angForm.get('name')?.dirty || angForm.get('name')?.touched)" class="alert alert-danger">
          Name is required.
        </div>
      </label>
      <label>Age:
        <input class="form-control" formControlName="age">
        <div *ngIf="angForm.get('age')?.invalid && (angForm.get('age')?.dirty || angForm.get('age')?.touched)" class="alert alert-danger">
          Age is required.
        </div>
      </label>
      <label>Address:
        <input class="form-control" formControlName="address">
        <div *ngIf="angForm.get('address')?.invalid && (angForm.get('address')?.dirty || angForm.get('address')?.touched)" class="alert alert-danger">
          Address is required.
        </div>
      </label>
    </div>
  </form>
</div>
